import { useEffect } from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { useToast } from './use-toast';
import { apiRequest } from '../lib/queryClient';
export function useComplianceAudit() {
    const queryClient = useQueryClient();
    const { toast } = useToast();
    // Fetch compliance audits from new clean API
    const { data: audits = [], isLoading } = useQuery({
        queryKey: ['/api/audit'],
        queryFn: async () => {
            try {
                console.log('ðŸ”„ Fetching audits from new clean API...');
                const response = await apiRequest('/api/audit');
                console.log('âœ… Clean audit API response:', response);
                return response.audits || [];
            }
            catch (error) {
                console.error('âŒ Error fetching clean audits:', error);
                return [];
            }
        },
    });
    // Get draft audits
    const draftAudits = audits.filter((audit) => audit.status === 'draft');
    // Get submitted audits - with debugging
    const submittedAudits = audits.filter((audit) => audit.status === 'submitted');
    // Debug logging for status tracking
    useEffect(() => {
        console.log(`ðŸ“Š Audit Status Debug:
    - Total audits: ${audits.length}
    - Draft audits: ${draftAudits.length}
    - Submitted audits: ${submittedAudits.length}
    - Status breakdown:`, audits.map((a) => ({ id: a.id, status: a.status, company: a.company })));
    }, [audits.length, draftAudits.length, submittedAudits.length]);
    // Create audit mutation using new clean API
    const createAuditMutation = useMutation({
        mutationFn: async (auditData) => {
            console.log('ðŸ†• Creating audit via clean API:', auditData);
            const response = await apiRequest('/api/audit', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(auditData),
            });
            return response.id;
        },
        onSuccess: () => {
            queryClient.invalidateQueries({ queryKey: ['/api/audit'] });
            toast({
                title: "Audit created",
                description: "Compliance audit draft created successfully.",
            });
        },
        onError: (error) => {
            console.error('âŒ Error creating audit:', error);
            toast({
                title: "Error creating audit",
                description: "Failed to create compliance audit. Please try again.",
                variant: "destructive",
            });
        },
    });
    // Update audit mutation using new clean API
    const updateAuditMutation = useMutation({
        mutationFn: async ({ id, data }) => {
            console.log('ðŸ’¾ HOOK: Updating audit via clean API', id, 'with data:', data);
            const response = await apiRequest(`/api/audit/${id}`, {
                method: 'PUT',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(data),
            });
            console.log('âœ… HOOK: Clean audit update successful');
            return id;
        },
        onSuccess: () => {
            console.log('âœ… HOOK: Invalidating cache after successful update');
            queryClient.invalidateQueries({ queryKey: ['/api/audit'] });
        },
        onError: (error) => {
            console.error('âŒ HOOK: Error updating audit:', error);
            toast({
                title: "Error saving audit",
                description: "Failed to save audit changes. Please try again.",
                variant: "destructive",
            });
        },
    });
    // Submit audit mutation using new clean API
    const submitAuditMutation = useMutation({
        mutationFn: async (id) => {
            const submittedAt = new Date().toISOString();
            console.log('ðŸš€ Frontend: Submitting audit via clean API with data:', { status: 'submitted', submittedAt });
            const response = await apiRequest(`/api/audit/${id}`, {
                method: 'PUT',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                    status: 'submitted',
                    submittedAt: submittedAt,
                }),
            });
            return { id, response, submittedAt };
        },
        onSuccess: async (result) => {
            // Force immediate cache refresh using new API key
            await queryClient.invalidateQueries({ queryKey: ['/api/audit'] });
            await queryClient.refetchQueries({ queryKey: ['/api/audit'] });
            // Also update the cache optimistically
            queryClient.setQueryData(['/api/audit'], (oldData) => {
                if (!oldData?.audits)
                    return oldData;
                return {
                    ...oldData,
                    audits: oldData.audits.map((audit) => audit.id === result.id
                        ? { ...audit, status: 'submitted', submittedAt: result.submittedAt }
                        : audit)
                };
            });
            console.log('âœ… Audit submitted successfully, cache updated');
            toast({
                title: "Audit submitted",
                description: "Compliance audit submitted successfully.",
            });
        },
        onError: (error) => {
            console.error('Error submitting audit:', error);
            toast({
                title: "Error submitting audit",
                description: "Failed to submit audit. Please try again.",
                variant: "destructive",
            });
        },
    });
    // Get single audit with full data preservation
    const getAudit = async (id) => {
        try {
            // First try to find in current audits list
            let audit = audits.find((a) => a.id === id);
            if (!audit) {
                // If not found locally, fetch directly from server
                console.log('ðŸ” Fetching audit from new clean API server:', id);
                const response = await apiRequest(`/api/audit/${id}`);
                audit = response.audit;
            }
            if (audit) {
                console.log('ðŸ“‹ Retrieved audit with parts:', audit.parts?.length || 0);
                // Log sample data to verify preservation
                if (audit.parts?.[0]?.items?.[0]) {
                    console.log('ðŸ“ Sample item data:', {
                        response: audit.parts[0].items[0].response,
                        remark: audit.parts[0].items[0].remark,
                        images: audit.parts[0].items[0].evidenceImages?.length || 0
                    });
                }
            }
            return audit || null;
        }
        catch (error) {
            console.error('Error fetching audit:', error);
            return null;
        }
    };
    // Delete audit mutation using new clean API
    const deleteAuditMutation = useMutation({
        mutationFn: async (id) => {
            const response = await apiRequest(`/api/audit/${id}`, {
                method: 'DELETE',
            });
            return id;
        },
        onSuccess: () => {
            queryClient.invalidateQueries({ queryKey: ['/api/audit'] });
            toast({
                title: "Audit deleted",
                description: "Compliance audit deleted successfully.",
            });
        },
        onError: (error) => {
            console.error('Error deleting audit:', error);
            toast({
                title: "Error deleting audit",
                description: "Failed to delete audit. Please try again.",
                variant: "destructive",
            });
        },
    });
    // Auto-save function DISABLED to prevent double saves
    const autoSave = async (id, data) => {
        console.log('ðŸš« AUTO-SAVE DISABLED: Preventing double save conflicts');
        // Auto-save disabled to prevent double save bug
        // Only manual saves are allowed
    };
    return {
        audits,
        draftAudits,
        submittedAudits,
        isLoading,
        createAudit: createAuditMutation.mutateAsync,
        updateAudit: updateAuditMutation.mutateAsync,
        submitAudit: submitAuditMutation.mutateAsync,
        deleteAuditMutation,
        getAudit,
        autoSave,
        isCreating: createAuditMutation.isPending,
        isUpdating: updateAuditMutation.isPending,
        isSubmitting: submitAuditMutation.isPending,
    };
}

import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
const BASE_URL = import.meta.env.DEV ? 'http://localhost:5000' : '';
export function useAuditForms(company, status, auditType) {
    const queryClient = useQueryClient();
    const queryParams = new URLSearchParams();
    if (company)
        queryParams.append('company', company);
    if (status)
        queryParams.append('status', status);
    if (auditType)
        queryParams.append('auditType', auditType);
    const { data: auditForms = [], isLoading, error } = useQuery({
        queryKey: ['/api/audit-forms', company, status, auditType],
        queryFn: async () => {
            const response = await fetch(`${BASE_URL}/api/audit-forms?${queryParams}`);
            if (!response.ok)
                throw new Error('Failed to fetch audit forms');
            return response.json();
        }
    });
    const createMutation = useMutation({
        mutationFn: async (auditForm) => {
            const response = await fetch(`${BASE_URL}/api/audit-forms`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(auditForm)
            });
            if (!response.ok)
                throw new Error('Failed to create audit form');
            return response.json();
        },
        onSuccess: () => {
            queryClient.invalidateQueries({ queryKey: ['/api/audit-forms'] });
        }
    });
    const updateMutation = useMutation({
        mutationFn: async ({ id, ...auditForm }) => {
            const response = await fetch(`${BASE_URL}/api/audit-forms/${id}`, {
                method: 'PUT',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(auditForm)
            });
            if (!response.ok)
                throw new Error('Failed to update audit form');
            return response.json();
        },
        onSuccess: () => {
            queryClient.invalidateQueries({ queryKey: ['/api/audit-forms'] });
        }
    });
    const deleteMutation = useMutation({
        mutationFn: async (id) => {
            const response = await fetch(`${BASE_URL}/api/audit-forms/${id}`, {
                method: 'DELETE'
            });
            if (!response.ok)
                throw new Error('Failed to delete audit form');
            return response.json();
        },
        onSuccess: () => {
            queryClient.invalidateQueries({ queryKey: ['/api/audit-forms'] });
        }
    });
    return {
        auditForms,
        isLoading,
        error,
        createAuditForm: createMutation.mutate,
        updateAuditForm: updateMutation.mutate,
        deleteAuditForm: deleteMutation.mutate,
        isCreating: createMutation.isPending,
        isUpdating: updateMutation.isPending,
        isDeleting: deleteMutation.isPending
    };
}
export function useAuditForm(id) {
    const { data: auditForm, isLoading, error } = useQuery({
        queryKey: ['/api/audit-forms', id],
        queryFn: async () => {
            const response = await fetch(`${BASE_URL}/api/audit-forms/${id}`);
            if (!response.ok)
                throw new Error('Failed to fetch audit form');
            return response.json();
        },
        enabled: !!id
    });
    return { auditForm, isLoading, error };
}

import { Router } from 'express';
import { generateAuditPDF, auditPDFGenerator } from '../auditPdfGenerator';
import { generateComplianceAuditPDF } from '../pdfMaster';

import fs from 'fs/promises';
import path from 'path';

const router = Router();

// Import Firebase admin
import { adminDb } from '../firestoreHelpers.js';

// Helper function to convert Firestore timestamp to ISO string
function convertFirestoreDate(timestamp: any): string | undefined {
  if (!timestamp) return undefined;
  if (typeof timestamp === 'string') return timestamp;
  if (timestamp.toDate) return timestamp.toDate().toISOString();
  if (timestamp._seconds) return new Date(timestamp._seconds * 1000).toISOString();
  return undefined;
}

/**
 * Generate PDF for compliance audit
 * GET /api/audits/compliance/:id/pdf
 */
router.get('/compliance/:id/pdf', async (req, res) => {
  try {
    const { id } = req.params;

    if (!id) {
      return res.status(400).json({
        success: false,
        error: 'Audit ID is required'
      });
    }

    console.log(`ðŸ“„ Generating PDF for compliance audit ID: ${id}`);

    // Fetch audit data from Firebase
    const auditDoc = await adminDb.collection('complianceAudits').doc(id).get();

    if (!auditDoc.exists) {
      return res.status(404).json({
        success: false,
        error: 'Audit not found'
      });
    }

    const auditData = auditDoc.data();

    if (!auditData) {
      return res.status(404).json({
        success: false,
        error: 'Audit data not found'
      });
    }

    // Use fallback PDF generator for reliable generation
    const { generateComplianceAuditPDFFallback } = await import('../pdfFallback');
    const pdfBuffer = await generateComplianceAuditPDFFallback(auditData);

    // Create filename with audit details
    const fileName = `compliance-audit-${auditData.company}-${id}-${Date.now()}.pdf`;
    const uploadsDir = path.join(process.cwd(), 'uploads');

    // Ensure uploads directory exists
    try {
      await fs.access(uploadsDir);
    } catch {
      await fs.mkdir(uploadsDir, { recursive: true });
    }

    // Save PDF to file
    const filePath = path.join(uploadsDir, fileName);
    await fs.writeFile(filePath, pdfBuffer);

    console.log(`âœ… PDF generated successfully: ${fileName} (${Math.round(pdfBuffer.length / 1024)}KB)`);

    // Set headers for PDF download
    res.setHeader('Content-Type', 'application/pdf');
    res.setHeader('Content-Disposition', `attachment; filename="${fileName}"`);
    res.setHeader('Content-Length', pdfBuffer.length.toString());

    // Send the PDF buffer directly
    res.send(pdfBuffer);
  } catch (error) {
    console.error('âŒ Error generating compliance audit PDF:', error);
    res.status(500).json({
      success: false,
      error: error instanceof Error ? error.message : 'PDF generation failed'
    });
  }
});

/**
 * Generate PDF for specific audit
 * POST /api/audits/:id/generate-pdf
 */
router.post('/:id/generate-pdf', async (req, res) => {
  try {
    const { id } = req.params;

    if (!id) {
      return res.status(400).json({
        success: false,
        error: 'Audit ID is required'
      });
    }

    console.log(`Generating PDF for audit ID: ${id}`);

    const result = await generateAuditPDF(id);

    if (result.success) {
      res.json({
        success: true,
