      res.status(500).json({
        success: false,
        error: result.error || 'Failed to generate PDF'
      });
    }
  } catch (error) {
    console.error('Error in PDF generation route:', error);
    res.status(500).json({
      success: false,
      error: error instanceof Error ? error.message : 'Internal server error'
    });
  }
});

