import admin from 'firebase-admin';
import fs from 'fs';

// Initialize Firebase Admin
try {
  const serviceAccount = JSON.parse(fs.readFileSync('./server/serviceAccountKey.json', 'utf8'));

  if (admin.apps.length === 0) {
    admin.initializeApp({
      credential: admin.credential.cert(serviceAccount),
      projectId: 'rugcraftpro'
    });
  }

  admin.firestore().settings({
    ignoreUndefinedProperties: true
  });

  const db = admin.firestore();

  async function testComplianceAPI() {
    console.log('ðŸ§ª Testing Compliance Audit API...');

    try {
      // Test 1: Get existing compliance audits
      console.log('\nðŸ“‹ Test 1: Getting existing compliance audits...');
      const auditsSnapshot = await db.collection('complianceAudits')
        .orderBy('updatedAt', 'desc')
        .limit(5)
        .get();

      console.log(`Found ${auditsSnapshot.size} existing compliance audits`);

      auditsSnapshot.forEach((doc, index) => {
        const data = doc.data();
        console.log(`  ${index + 1}. ${doc.id}: ${data.company} - ${data.status} (${data.auditorName})`);
      });

      // Test 2: Create a new compliance audit
      console.log('\nðŸ“ Test 2: Creating new compliance audit...');
      const now = new Date().toISOString();
      const testAudit = {
        company: 'EHI',
        auditorName: 'Test Auditor API',
        location: 'Test Location',
        auditScope: 'API Test Scope',
        auditDate: now.split('T')[0],
        checklist: [
          {
            code: 'C1',
            question: 'Test question 1?',
            response: 'Yes',
            remarks: 'API test remark 1'
          },
          {
            code: 'C2', 
            question: 'Test question 2?',
            response: 'No',
            remarks: 'API test remark 2'
          }
        ],
        status: 'draft',
        createdBy: 'API Test',
        createdAt: now,
        updatedAt: now
      };

      const docRef = await db.collection('complianceAudits').add(testAudit);
      console.log(`âœ… Created test audit: ${docRef.id}`);

      // Test 3: Update the audit
      console.log('\nðŸ“ Test 3: Updating the audit...');
      const updateData = {
        ...testAudit,
        status: 'submitted',
        submittedAt: new Date().toISOString(),
        updatedAt: new Date().toISOString(),
        checklist: [
          ...testAudit.checklist,
          {
            code: 'C3',
            question: 'Updated test question 3?',
            response: 'NA',
            remarks: 'Added during update test'
          }
        ]
      };

      await docRef.update(updateData);
      console.log('âœ… Audit updated successfully');

      // Test 4: Verify the update
      console.log('\nðŸ” Test 4: Verifying the update...');
      const updatedDoc = await docRef.get();
      if (updatedDoc.exists) {
        const data = updatedDoc.data();
        console.log(`âœ… Verified update: Status is ${data.status}, ${data.checklist.length} checklist items`);
      }

      // Test 5: Check if API routes are working
      console.log('\nðŸŒ Test 5: Checking server API...');
      try {
        // Try to make HTTP request to local server
        const response = await fetch('http://localhost:5000/api/v2/compliance-audits', {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json'
          }
        });

        if (response.ok) {
          const data = await response.json();
          console.log(`âœ… API endpoint working: Found ${data.audits?.length || 0} audits via HTTP`);
        } else {
          console.log(`âš ï¸ API endpoint returned status: ${response.status}`);
        }
      } catch (fetchError) {
        console.log('âš ï¸ Server not running or API not accessible via HTTP');
      }

      // Clean up test data
      console.log('\nðŸ§¹ Cleaning up test data...');
      await docRef.delete();
      console.log('âœ… Test audit deleted');

    } catch (error) {
      console.error('âŒ Test failed:', error);
    }
  }

  testComplianceAPI().then(() => {
    console.log('\nðŸŽ‰ Compliance API test complete!');
    process.exit(0);
  }).catch(error => {
    console.error('âŒ Test error:', error);
    process.exit(1);
  });

} catch (error) {
  console.error('âŒ Firebase initialization failed:', error);
  process.exit(1);
}

